    public static ArrayList<Food> getListData() {
        Food food = null;
        ArrayList<Food> list = new ArrayList<>();
        for (String[] aData : data) {
            food = new Food();
            food.setName(aData[0]);
            food.setRemarks(aData[1]);
            food.setPhoto(aData[2]);
            list.add(food);
        }
        return list;
    }
